mapper.select:
SELECT bak.id,bak.real_price as price,bak.discount_state,bak.selling_price AS discount_price , bak.`name` AS title ,bak.create_time FROM (
SELECT id,real_price, discount_state,selling_price,`name`,create_time FROM w_set_menu WHERE create_time = (SELECT MAX(create_time) FROM w_set_menu WHERE shop_id=#{shopId}) AND deleted=0 AND state=1 AND shop_id=#{shopId}
UNION 
SELECT id, price,discount_state,discount_price,title, create_time FROM w_goods WHERE create_time = (SELECT MAX(create_time) FROM w_goods WHERE shop_id=#{shopId}) AND deleted=0 AND is_putaway=1 AND shop_id=#{shopId}
)as bak
ORDER BY bak.create_time DESC LIMIT 0,1

SELECT * FROM w_goods WHERE type=1 AND deleted=0 AND is_putaway=1 AND state=0 AND shop_id IN (
SELECT
id
FROM
w_new_shop t1
WHERE
round (
(
6378.138 * 2 * ASIN(
SQRT(
POW( SIN( ( #{query.latitude} * PI( ) / 180 - t1.latitude * PI( ) / 180 ) / 2 ), 2 ) + COS( #{query.latitude}  * PI( ) / 180 ) * COS( t1.latitude * PI( ) / 180 ) * POW( SIN( ( #{query.longitude} * PI( ) / 180 - t1.longitude * PI( ) / 180 ) / 2 ), 2 )
)
) * 1000
)
) < 3000 AND t1.deleted=0 AND t1.state = 1 AND t1.shielding = 0 AND FIND_IN_SET(6,shop_tree_id)> 0
)

SELECT *,(
SELECT round (
(
6378.138 * 2 * ASIN(
SQRT(
POW( SIN( ( #{shopQuery.latitude} * PI( ) / 180 - t1.latitude * PI( ) / 180 ) / 2 ), 2 ) + COS( #{shopQuery.latitude}  * PI( ) / 180 ) * COS( t1.latitude * PI( ) / 180 ) * POW( SIN( ( #{shopQuery.longitude} * PI( ) / 180 - t1.longitude * PI( ) / 180 ) / 2 ), 2 )
)
) * 1000
)
) as d  from w_new_shop t1 where id = s.shop_id
) as distance

FROM w_goods s WHERE type=1 AND deleted=0 AND is_putaway=1 AND state=0

    AND s.title LIKE CONCAT('%',#{shopQuery.keyword,jdbcType=VARCHAR},'%')

ORDER BY distance limit 0,5
